/* ====================================================================== */
/* ============================ Home Section ============================ */
/* ====================================================================== */

.home {
    .video {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;
        z-index: -1;
    }

    .container {
        position: absolute;
        height: 100%;
        width: 100%;

        .content {
            position: relative;
            top: 85%;
            transform: translateY(-85%);
            padding: 10px 20px;

            background: rgba(33, 37, 41, 0.3);
            box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
            backdrop-filter: blur(10px);

            .line {
                height: 3px;
                width: 100%;
                background: rgba(33, 37, 41, 0.7);
                box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
                backdrop-filter: blur(10px);
                margin-bottom: 20px;
            }

            .title {
                font-family: $fontFamilyPrimary;
                font-size: $fontLarge;
                font-weight: $fontWeightBold;
                color: $colorLight;
                margin: 10px 0;
                letter-spacing: 5px;
            }


            .subtitle {
                font-family: $fontFamilyPrimary;
                font-size: $fontSmall;
                margin: 0 0 20px 0;
                color: $colorLight;

                span {
                    font-weight: 700;
                    color: $colorDark;
                    text-shadow:
                    0 0 10px $colorDark;
                }
            }
        }
    }
}
